import React, { Component } from 'react';


class SignUp extends Component {
  constructor() {
    super();
    this.state = {
      email: '',
      name: '',
      lastname: '',
      password: '',
      passwordConfirm: ''
    };

    this.updateEmailField = this.updateEmailField.bind(this);
    this.updateLastnameField = this.updateLastnameField.bind(this);
    this.updateNameField = this.updateNameField.bind(this);
    this.updatePasswordField = this.updatePasswordField.bind(this);
    this.updatePasswordConfirmField = this.updatePasswordConfirmField.bind(
      this
    );
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  updateEmailField(event) {
    this.setState({ email: event.target.value });
  }

  updateNameField(event) {
    this.setState({ name: event.target.value });
  }

  updateLastnameField(event) {
    this.setState({ lastname: event.target.value });
  }

  updatePasswordField(event) {
    this.setState({ password: event.target.value });
  }

  updatePasswordConfirmField(event) {
    this.setState({ passwordConfirm: event.target.value });
  }

  handleSubmit(event) {
    event.preventDefault();
    console.log(this.state);
  }

  render() {
    const { email, name, lastname, password, passwordConfirm } = this.state;
    return (
      <form onSubmit={this.handleSubmit}>
        <div className='segment'>
          <h1>Sign up</h1>
        </div>
        <label>
          <input
            value={name}
            onChange={this.updateNameField}
            type='text'
            placeholder='name'
          />
        </label>
        <label>
          <input
            onChange={this.updateLastnameField}
            value={lastname}
            type='text'
            placeholder='Lastname'
          />
        </label>
        <label>
          <input
            value={email}
            onChange={this.updateEmailField}
            type='email'
            placeholder='Email Address'
          />
        </label>
        <label>
          <input
            value={password}
            onChange={this.updatePasswordField}
            type='password'
            placeholder='Password'
          />
        </label>
        <label>
          <input
            value={passwordConfirm}
            onChange={this.updatePasswordConfirmField}
            type='password'
            placeholder='Password'
          />
        </label>
        <button className='red' type='submit'>
          <i className='icon ion-md-lock'></i> Log in
        </button>
      </form>
    );
  }
}

export default SignUp;
